var gapReady=$.Deferred();var jqmReady=$.Deferred();document.addEventListener("deviceReady",deviceReady,false);function deviceReady(){gapReady.resolve()}$(document).one("mobileinit",function(){jqmReady.resolve()});$.when(gapReady,jqmReady).then(myAppLogic);function myAppLogic(){var a=$('a[data-rel="external"]');$.each(a,function(){currentLink=$(this).attr("href");$(this).attr("href","#");$(this).attr("onclick",'openDeviceBrowser("'+currentLink+'")')})};